/*global kakao*/ 
import React, { useContext, useEffect, useState } from 'react'
import { LatLngContext } from '../../contexts/LatLngContext';

const Map = () =>{
    const {isLatLng, setLatLng} = useContext(LatLngContext);
    console.log(isLatLng)

    // const [LatLng, setLatLng] = useState([36.142410487698, 128.39430145218606]);
    var map;

    useEffect(()=>{
        var mapContainer = document.getElementById('map'), // 지도를 표시할 div  
        mapOption = { 
            center: new kakao.maps.LatLng(36.142410487698, 128.39430145218606), // 지도의 중심좌표
            level: 4 // 지도의 확대 레벨
        };

        map = new kakao.maps.Map(mapContainer, mapOption); // 지도를 생성합니다
    });


    return (
        <div id="map" style={{height: "100%"}}></div> 
    )
};

export default Map;
